<?php	$db_list = $sysConfig->loadDB();		$id = $pr->post('id');	$oldID = $pr->post('old_id',false);		if($oldID != $id){ 		if(isset($db_list[$id])){$pr->addAlertBox("<i18n>db:err:uidExists;{$id}</i18n>")->set('CloseWin',false)->response();}			unset($db_list[$oldID]);	}		$db_list[$id] = array(		'des' => $pr->getString('des'),		'DB' => 'SQLITE3',		'hide' => $pr->post('hide')	);		$sysConfig->saveDB($db_list);	$pr->addScript('pi.requestOnLoad("cerca_db");')->response();?>