<?php	$db_list = $sysConfig->loadDB();		$id = $pr->post('id');	$oldID = $pr->post('old_id',false);		if($oldID != $id){ 		if(isset($db_list[$id])){$pr->addAlertBox("La sorgente dati <b>{$id}</b> &eacute; gia in uso!")->set('CloseWin',false)->response();}		$old_pwd = $db_list[$oldID]['dbpwd'];		unset($db_list[$oldID]);	}else{		$old_pwd = $db_list[$id]['dbpwd'];	}	$new_pwd = $pr->post('dbpwd') ?: $old_pwd;		$db_list[$id] = array(		'des' => $pr->getString('des'),		'DB' => 'MYSQL',		'server' => $pr->getString('server'),//parse_string($_POST['server']),		'dbuser' => $pr->getString('dbuser'),//parse_string($_POST['dbuser']), 		'dbpwd' =>  $new_pwd,		'dbname' => $pr->getString('dbname'),//parse_string($_POST['dbname']), 		'userid' => $pr->post('userid'), // default 0 (per usi futuri)		'userpwd' => $pr->post('userpwd'), // default 0 (per usi futuri)		'hide' => $pr->post('hide')	);		$sysConfig->saveDB($db_list);	$pr->addScript('pi.requestOnLoad("cerca_db");')->response();?>