<?php	$grp_list = $sysConfig->loadGrp();	$mod_list = $sysConfig->loadMod();		$grp_sel ='<select name="grp" class="std">';	$id = $pr->post('ID');	foreach($grp_list as $k => $v){		$grp_sel.='<option value="'.$k.'" '.($mod_list[$id]["grp"]==$k ? 'selected' : '').'>'.$k.' - '.$v["nome"].'</option>';	}	$grp_sel.='</select>';	switch($mod_list[$id]["stato"]){		case 'ATT' : $sts = 'ATT - Attivo' ; break;		case 'DEV' : $sts = 'DEV - Sviluppo' ; break;		case 'ERR' : $sts = 'ERR - Errori Critici' ; break;		case 'PRIV' : $sts = 'PRIV - Privato' ; break;		case 'DIS' : $sts = 'DIS - Disabilitato' ; break;	}	$out='<div class="focus green"> Modifica il gruppo del modulo</div>		<div style="text-align:left;">		<div id="Mod_Del">			<input type="hidden" name="Mid" value="'.$id.'">			<input type="hidden" name="Q" value="Del">		</div>		<div id="mod_mod">			<table class="form separate">				<tr>					<th style="text-align:right; border-right:2px #080 solid;">Id Modulo</th>					<td>						'.$id.'						<input type="hidden" name="ID" value="'.$id.'" id="MID">						<input type="hidden" name="icon" value="'.$mod_list[$id]["iconfile"].'">						<input type="hidden" name="Q" value="Grp_Save_Mod">					</td>				</tr>				<tr>					<th style="text-align:right; border-right:2px #080 solid;">Nome</th>					<td>'.$mod_list[$id]["nome"].'</td>				</tr>				<tr>					<th style="text-align:right; border-right:2px #080 solid;">Descrizione</th>					<td>'.$mod_list[$id]["des"].'</td>				</tr>				<tr>					<th style="text-align:right; border-right:2px #080 solid;">Percorso</th>					<td>'.$mod_list[$id]["path"].'</td>				</tr>				<tr>					<th style="text-align:right; border-right:2px #080 solid;">Stato</th>					<td>'.$sts.'</td>				</tr>				<tr>					<th style="text-align:right; border-right:2px #080 solid;" > Gruppo </th>					<td>'.$grp_sel.'</td>				</tr>			</table>		</div><br>';	$footer = '<button class="red" onclick="pi.win.close();"> Annulla </button> <button class="green" onclick="pi.requestOnModal(\'mod_mod\')">Salva</button>';			$pr->addWindow(400,0,'Moduli',$out,$footer)->response();?>